sys_script_include.script:
var ScheduledUtils = Class.create();
ScheduledUtils.prototype = {

	initialize: function() {
    },

	scanRegisteredApps: function() {

		var regApp = new global.GlideQuery("x_1780675_config_0_registered_app")
		.where("u_active", true)
		.select("ci_retrieval_method", "u_last_scan")
		.forEach(function(row) {
			if(row.ci_retrieval_method == "assoc") {
				this.retrieveMappedCIsAssoc(row);
			} else if (regApp.ci_retrieval_method == "traverse") {
				this.retrieveMappedCIsTraverse(row);
			}
			row.update({ u_last_scan: new GlideDateTime() });
		});

	},

	retrieveMappedCIsAssoc: function(regApp) {
		gs.info("Retrieve apps via assoc" + regApp.getDisplayValue("u_service_instance"));
	},

	retrieveMappedCIsTraverse: function(regApp) {
		gs.info("Retrieve apps via traverse" + regApp.getDisplayValue("u_service_instance"));
	},

    type: 'ScheduledUtils'
};
